import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    Divider,
    List,
    ListItem,
    ListItemText,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        padding: 40,
        paddingTop: 100,
        backgroundColor: 'white',
        color: '#0000a2',
    },
    divider: {
        marginBlockStart: '0.5em',
        width: '60%',
        backgroundColor: 'rgb(0, 0, 0, 0.2)',
        marginBottom: 60,
    },
}));

const onSiteList = [
    {
        text: `New computer installation`,
    },
    {
        text: `E-Mail setup`,
    },
    {
        text: `Software installation`,
    },
    {
        text: `Data backup and migrations`,
    },
    {
        text: `Network toubleshooting`,
    },
    {
        text: `Network Storage Device installation`,
    },
    {
        text: `Server installations and migrations`,
    },
    {
        text: `Network printers/scanner deployments`,
    },
    {
        text: `Router/Firewall configuration`,
    },
    {
        text: `Fiber/Cable/T1 implementations`,
    },
];

const MidSection = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} item xs={12}>

            <Typography>
                We offer various levels of service
            </Typography>

            <Divider className={classes.divider} />

            <List>

                {
                    onSiteList.map((item) => {
                        return (
                            <ListItem>
                                <ListItemText
                                    primary={item.text}
                                />
                            </ListItem>
                        )
                    })
                }

            </List>

        </Grid>
    );
};

export default MidSection;
