import React, {
    useState,
    useContext,
} from 'react';

import { MediaContext } from '../../context/Media';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    Divider,
    List,
    ListItem,
    ListItemText,
    Collapse,
    Icon,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        paddingTop: 100,
        backgroundColor: 'white',
        color: '#0000a2',
        padding: '0 25%',
        [theme.breakpoints.down('md')]: {
            padding: '0 15%',
        },
        [theme.breakpoints.down('xs')]: {
            padding: 40,
        },
    },
    divider: {
        marginBlockStart: '0.5em',
        width: '60%',
        backgroundColor: 'rgb(0, 0, 0, 0.2)',
        marginBottom: 60,
    },
    nested: {
        paddingLeft: theme.spacing(4),
        color: 'black',
    }
}));

const onSiteList = [
    {
        text: `New computer installation`,
    },
    {
        text: `E-Mail setup`,
    },
    {
        text: `Software installation`,
    },
    {
        text: `Data backup and migrations`,
    },
    {
        text: `Network toubleshooting`,
    },
    {
        text: `Network Storage Device installation`,
    },
    {
        text: `Server installations and migrations`,
    },
    {
        text: `Network printers/scanner deployments`,
    },
    {
        text: `Router/Firewall configuration`,
    },
    {
        text: `Fiber/Cable/T1 implementations`,
    },
];

const shopList = [
    {
        text: `Computer troubleshooting and repair`,
    },
    {
        text: `Operating System installations and upgrades`
    },
    {
        text: `Hardware upgrades`,
    },
    {
        text: `Data backup & recovery`
    },
];

const remoteList = [
    {
        text: `Assisted software installation`,
    },
    {
        text: `Basic printer installation`,
    },
    {
        text: `Simple e-mail setup`,
    },
    {
        text: `Minor troubleshooting`,
    },
    {
        text: `Training`,
    },
];

const MidSection = () => {

    const classes = useStyles();
    const media = useContext(MediaContext);

    const [onsite, setOnsite] = useState(true);
    const [shop, setShop] = useState(true);
    const [remote, setRemote] = useState(true);

    return (
        <Grid className={classes.container} item xs={12}>

            <Typography
                variant={media.xs ? 'h6' : 'h5'}
            >
                We offer various levels of service
            </Typography>

            <Divider className={classes.divider} />

            <List>

                <ListItem
                    button
                    onClick={() => setOnsite(!onsite)}
                >
                    <ListItemText
                        primary="Onsite servcie at an hourly rate of $95.00 billed to the 
                        1/2 hour."
                    />
                    <Icon>{onsite ? 'expand_less' : 'expand_more'}</Icon>
                </ListItem>
                <Collapse in={onsite} timeout="auto" unmountOnExit>
                    {
                        onSiteList.map((item) => {
                            return (
                                <ListItem className={classes.nested}>
                                    <ListItemText
                                        primary={item.text}
                                    />
                                </ListItem>
                            )
                        })
                    }
                </Collapse>

                <ListItem
                    button
                    onClick={() => setShop(!shop)}
                >
                    <ListItemText
                        primary="Shop service at $80.00 per hour or fixed rate projects"
                    />
                    <Icon>{shop ? 'expand_less' : 'expand_more'}</Icon>
                </ListItem>
                <Collapse in={shop} timeout="auto" unmountOnExit>
                    {
                        shopList.map((item) => {
                            return (
                                <ListItem className={classes.nested}>
                                    <ListItemText
                                        primary={item.text}
                                    />
                                </ListItem>
                            )
                        })
                    }
                </Collapse>

                <ListItem
                    button
                    onClick={() => setRemote(!remote)}
                >
                    <ListItemText
                        primary="Remote Service at $70.00 per session"
                    />
                    <Icon>{remote ? 'expand_less' : 'expand_more'}</Icon>
                </ListItem>
                <Collapse in={remote} timeout="auto" unmountOnExit>
                    {
                        remoteList.map((item) => {
                            return (
                                <ListItem className={classes.nested}>
                                    <ListItemText
                                        primary={item.text}
                                    />
                                </ListItem>
                            )
                        })
                    }
                </Collapse>

            </List>

        </Grid>
    );
};

export default MidSection;
