import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    Divider,
    Icon,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        padding: 40,
    },
    divider: {
        width: '40%',
        marginBlockStart: '0.5em',
        marginBottom: 40,
    },
    pillarsIntro: {
        width: '100%',
    },
    pillarsHeaders: {
        width: '100%',
        marginBottom: 0,
    },
    icons: {
        fontSize: 100,
    },
}));

const Pillars = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} item xs={12}>

            <Typography
                className={classes.pillarsIntro}
                align="center"
                variant="h6"
            >
                These are the five pillars of our successful IT strategy:
            </Typography>

            <Typography
                className={classes.pillarsHeaders}
                align="center"
                variant="h5"
            >
                Assess
            </Typography>

            <Icon
                className={classes.icons}
            >
                equalizer
            </Icon>

            <Divider className={classes.divider} />

            <Typography>
                Our case-specific technology audit and data gathering provides us with a detailed and 
                comprehensive picture of your existing network architecture, IT infrastructure and working 
                environment. Our goal is to make state-of-the-art technology work for you.
            </Typography>

        </Grid>
    );
};

export default Pillars;
