import React, {
    useState,
} from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    TextField,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        padding: 40,
        paddingTop: 100,
        backgroundColor: 'white',
        color: '#0000a2',  
    },
}));

const initInfo = {
    name: '',
    email: '',
    message: '',
};

const Form = () => {

    const [info, setInfo] = useState({ ...initInfo });

    return (
        <Grid className={classes.container} item xs={12}>

            <TextField
                variant="outlined"
                label="Name"
                value={info.name}
                onChange={(e) => setInfo({ ...info, name: e.target.value})}
            />

            <TextField
                variant="outlined"
                label="Email"
                value={info.email}
                onChange={(e) => setInfo({ ...info, email: e.target.value})}
            />

            <TextField
                variant="outlined"
                label="Message"
                multiline
                value={info.message}
                onChange={(e) => setInfo({ ...info, message: e.target.value})}
            />

        </Grid>
    );
};

export default Form;
