import React, {
    useState,
} from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Drawer,
    Button,
    List,
    ListItem,
    ListItemIcon,
    ListItemText,
    Icon,
} from '@material-ui/core';

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  button: {
    padding: 10,
    minWidth: 0,
  },
  icon: {
    color: 'white',
  },
  iconTrigger: {
    color: '#0000a2',
  },
});

const drawerContent = [
  {
    text: 'Home',
    icon: 'home',
    link: '/home',
  },
  {
    text: 'About',
    icon: 'account_circle',
    link: '/about',
  },
  {
    text: 'Services',
    icon: 'developer_mode',
    link: '/services',
  },
  {
    text: 'Strategy',
    icon: 'insert_chart',
    link: '/strategy',
  },
  {
    text: 'Pricing',
    icon: 'local_atm',
    link: '/pricing',
  },
  {
    text: 'Technologies',
    icon: 'timeline',
    link: '/technologies',
  },
  {
    text: 'Partnerships',
    icon: 'group',
    link: '/partnerships',
  },
  {
    text: 'Testimonials',
    icon: 'chat',
    link: '/testimonials',
  },
  {
    text: 'Contact Us',
    icon: 'phone',
    link: '/contact',
  },
];

const TempDrawer = (props) => {
  const classes = useStyles();
  const [state, setState] = useState(false);

  const toggleDrawer = (open) => event => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }
    setState(open);
  };

  const sideList = () => (
    <div
      className={classes.list}
      role="presentation"
      onClick={toggleDrawer(false)}
      onKeyDown={toggleDrawer(false)}
    >
      <List>
        {
          drawerContent.map((item) => (
            <ListItem button key={item.text}>
              <ListItemIcon>
                <Icon>
                  {item.icon}
                </Icon>
              </ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItem>
          ))
        }
      </List>
    </div>
  );

  return (
    <div>
        <Button
            className={classes.button}
            onClick={toggleDrawer(true)}
        >
            <Icon className={props.trigger ? classes.iconTrigger : classes.icon}>
                menu
            </Icon>
        </Button>

        <Drawer open={state} onClose={toggleDrawer(false)}>
            {sideList('left')}
        </Drawer>
    </div>
  );
};

export default TempDrawer;
