import React, {
    useState,
} from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Paper,
    Typography,
    Divider,
    TextField,
    Button,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        padding: 40,
        paddingTop: 100,
        backgroundColor: 'white',
    },
    paper: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        padding: 20,
        color: '#0000a2',    
    },
    header: {
        width: '100%',
    },
    divider: {
        marginBlockStart: '0.5em',
        width: '60%',
        marginBottom: 20,
        backgroundColor: 'rgb(0, 0, 0, 0.2)',
    },
    textFields: {
        marginBottom: 20,
    },
    button: {
        padding: 10,
        width: '100%',
        backgroundColor: '#0000a2',
        color: 'white',
    },
}));

const initInfo = {
    name: '',
    email: '',
    message: '',
};

const Form = () => {

    const classes = useStyles();

    const [info, setInfo] = useState({ ...initInfo });

    return (
        <Grid className={classes.container} item xs={12}>

            <Paper className={classes.paper}>

                <Typography
                    className={classes.header}
                    variant="h4"
                    align="center"
                >
                    Form
                </Typography>

                <Divider className={classes.divider} />

                <TextField
                    className={classes.textFields}
                    variant="outlined"
                    label="Name"
                    value={info.name}
                    onChange={(e) => setInfo({ ...info, name: e.target.value})}
                />

                <TextField
                    className={classes.textFields}
                    variant="outlined"
                    label="Email"
                    value={info.email}
                    onChange={(e) => setInfo({ ...info, email: e.target.value})}
                />

                <TextField
                    className={classes.textFields}
                    variant="outlined"
                    label="Message"
                    rows="5"
                    multiline
                    value={info.message}
                    onChange={(e) => setInfo({ ...info, message: e.target.value})}
                />

                <Button
                    className={classes.button}
                >
                    <Typography>
                        Submit
                    </Typography>
                </Button>

            </Paper>


        </Grid>
    );
};

export default Form;
