import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    Divider,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        height: '100vh',
        padding: 40,
    },
    divider: {
        width: '40%',
        marginBlockStart: '0.5em',
    },
    pillarsHeader: {
        width: '100%',
    },
}));

const Pillars = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} item xs={12}>

            <Typography
                className={classes.pillarsHeader}
                align="center"
            >
                These are the five pillars of our successful IT strategy:
            </Typography>

            <Divider className={classes.divider} />

            <Typography>
                Assess
            </Typography>

            <Typography>
                Our case-specific technology audit and data gathering provides us with a detailed and 
                comprehensive picture of your existing network architecture, IT infrastructure and working 
                environment. Our goal is to make state-of-the-art technology work for you.
            </Typography>

        </Grid>
    );
};

export default Pillars;
