import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        padding: 40,
        paddingTop: 100,
        background: '#0000a2',
        color: 'white',
    },
    mailInfo: {
        width: '100%',
        marginBottom: 20,
    },
}));

const mailInfo = [
    {
        text: 'Peritus IT Solutions',
    },
    {
        text: 'PO Box 480835',
    },
    {
        text: 'Charlotte NC 28269',
    },
];

const Info = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} item xs={12}>

            <Typography
                className={classes.mailInfo}
            >
                Email: info@peritusit.com
            </Typography>

            <Typography
                className={classes.mailInfo}
            >
                Mailing Address
            </Typography>

            {
                mailInfo.map((item) => {
                    return (
                        <Typography
                            className={classes.mailInfo}
                            align="center"
                        >
                            {item.text}
                        </Typography>
                    )
                })
            }

        </Grid>
    );
};

export default Info;
