import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    Divider,
    Button,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        padding: 40,
        backgroundColor: '#0000a2',
        color: 'white',
    },
    bottomHeader: {
        marginTop: 100,
    },
    divider: {
        marginBlockStart: '0.5em',
        width: '60%',
        marginBottom: 40,
    },
    bottomBody: {
        width: '100%',
        marginBottom: 40,
    },
}));

const BottomSection = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} item xs={12}>

            <Typography
                className={classes.bottomHeader}
                align="center"
                variant="h4"
            >
                We're connected
            </Typography>

            <Divider className={classes.divider} />

            <Typography
                className={classes.bottomBody}
                align="center"
            >
                The success of your IT company also lies in its strategic alliances and 
                partnerships. Peritus IT Solutions has cultivated relationships with some 
                of the major industry players to provide you a complete technical support team.
            </Typography>

            <Typography
                className={classes.bottomBody}
                align="center"
            >
                View a list of our professional partnerships, alliances and supported 
                technologies.
            </Typography>

            <Button>
                <Typography>
                    Partnerships
                </Typography>
            </Button>

        </Grid>
    );
};

export default BottomSection;
