import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    Avatar,
    Divider
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        padding: 40,
        paddingTop: 100,
        backgroundColor: 'white',
    },
    avatars: {
        height: 80,
        width: 80,
    },
    nameHeaders: {
        width: '100%',
        marginTop: 10,
    },
    dividers: {
        marginBlockStart: '0.5em',
        width: '60%',
        backgroundColor: 'rgb(0, 0, 0, 0.2)',
        marginBottom: 30,
    },
}));

const Vouchers = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} item xs={12}>

            <Avatar
                className={classes.avatars}
                src={`https://media.licdn.com/dms/image/C5603AQEBtZyjmguZ4Q/profile-displayphoto-shrink_200_200/0?e=1575504000&v=beta&t=AUMFUiDeLOQaC1IUVlKc5LlokoTsTVgdeB76e4owS00`}
                alt="Maia Williams Photo"
            />

            <Typography
                className={classes.nameHeaders}
                align="center"
                variant="h5"
            >
                Maia Williams
            </Typography>

            <Divider className={classes.dividers} />

            <Typography
                className={classes.quotes}
                align="center"
                color="textSecondary"
            >
                “Randall understands IT and business...soup to nuts. He's an IT expert with 
                a great personality and a flair for recommending solutions that address 
                immediate IT needs and as a bonus, strengthen your systems. He's thoughtful, 
                pragmatic and honest. He's a keeper!”
            </Typography>

        </Grid>
    );
};

export default Vouchers;
