import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    Divider,
    Button,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        backgroundColor: 'white',
        height: '100vh',
        padding: 40,
        color: 'rgb(6, 62, 153)',
    },
    midHeader: {
        marginBottom: 10,
    },
    divider: {
        marginBlockStart: '0.5em',
        width: '50%',
        marginBottom: 60,
        backgroundColor: 'rgb(0, 0, 0, 0.2)',
    },
    midButton: {
        padding: 10,
        backgroundColor: 'rgb(0, 0, 0, 0.2)',
    },
}));

const MidSection = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} item xs={12}>

            <Typography
                className={classes.midHeader}
                variant="h5"
                align="center"
            >
                Unique solutions for your business
            </Typography>

            <Divider className={classes.divider} />

            <Typography
                align="center"
            >
                No two businesses are the same. We believe the technology supporting your business operations 
                should reflect the specific needs of your company. That's why we offer unique IT solutions.
                Click below to learn more about us and how our individual solutions can work for you.
            </Typography>

            <a
                className={classes.midLink}
                href="/about"
            >
                <Button
                    className={classes.midButton}
                >
                    <Typography>
                        More
                    </Typography>
                </Button>
            </a>

        </Grid>
    );
};

export default MidSection;
